import {rerenderTree} from "../render";

let state = {
    profilePage: {
        postsData: [
        {id: 1, post: 'Го по пиву пацаны'},
        {id: 2, post: ')'}
    ],
        newPost: 'raz raz'
    },
    dialogsPage: {
        messagesData: [
        {id: 1, message: 'Гослинг'},
        {id: 2, message: 'Вася'},
        {id: 3, message: 'Путин'}
    ],
        dialogsData: [
        {id: 1, name: 'Гослинг'},
        {id: 2, name: 'Вася'},
        {id: 3, name: 'Путин'},
        {id: 4, name: 'Трамп'},
        {id: 5, name: 'Обама'},
        {id: 6, name: 'Мэнсон'},
        {id: 7, name: 'Антошка'}
    ],
        newMessage: 'ya lomal steklo'
    }
}

export let addPost = () => {
    let newPost = {
        id: 3,
        post: state.profilePage.newPost
    };

    state.profilePage.postsData.push(newPost);
    state.profilePage.newPost = '';
    rerenderTree(state);
}

export let updatePost = (text) => {
    state.profilePage.newPost = text;
    rerenderTree(state);
}

export let sendMessage = () => {
    let newMessage = {
        id: 4,
        message: state.dialogsPage.newMessage
    };

    state.dialogsPage.messagesData.push(newMessage);
    state.dialogsPage.newMessage = '';
    rerenderTree(state);
}

export let updateMessage = (text) => {
    state.dialogsPage.newMessage = text;
    rerenderTree(state);
}

export default state;
