import React from "react";
import newp from "./NewPost.module.css"
import MyPosts from "../My posts/MyPosts";
import {addPostCreator, updatePostCreator} from "../../../redux/state";

const NewPost = (props) => {
    debugger;
    let postElements =
        props.state.postsData.map( posts => <MyPosts post={posts.post} />);

    let newPostElements = React.createRef();

    let addPost = () => {
        props.dispatch(addPostCreator());
    }

    let onPostChange = () => {
        let text = newPostElements.current.value;
        let newVar = updatePostCreator(text);
        props.dispatch(newVar);
    }

    return (
        <div>
            <div className={newp.postedit}>
                <span className={newp.mypost}>Мои посты</span>
                <div className={newp.text}>
                    <textarea className={newp.textarea} placeholder="Что нового?"
                              ref={newPostElements} value={props.newPost} onChange={onPostChange}/>
                    <button className={newp.btn} onClick={ addPost }><i className="fas fa-paper-plane"/></button>
                </div>
            </div>
            {postElements}
        </div>
    );
}

export default NewPost;
