let store = {
    _state: {
        profilePage: {
            postsData: [
                {id: 1, post: 'Го по пиву пацаны'},
                {id: 2, post: ')'}
            ],
            newPost: 'jopa'
        },
        dialogsPage: {
            messagesData: [
                {id: 1, message: 'Гослинг'},
                {id: 2, message: 'Вася'},
                {id: 3, message: 'Путин'}
            ],
            dialogsData: [
                {id: 1, name: 'Гослинг'},
                {id: 2, name: 'Вася'},
                {id: 3, name: 'Путин'},
                {id: 4, name: 'Трамп'},
                {id: 5, name: 'Обама'},
                {id: 6, name: 'Мэнсон'},
                {id: 7, name: 'Антошка'}
            ],
            newMessage: 'ya lomal steklo'

        }
    },
    getState() {
      return this._state;
    },
    _callSubscriber() {
        console.log('State changed');
    },
    _addPost() {
        let newPost = {
            id: 3,
            post: this._state.profilePage.newPost
        };

        this._state.profilePage.postsData.push(newPost);
        this._state.profilePage.newPost = '';
        this._callSubscriber(this._state);
    },
    _updatePost(text) {
        this._state.profilePage.newPost = text;
        this._callSubscriber(this._state);
    },
    _sendMessage() {
        let newMessage = {
            id: 4,
            message: this._state.dialogsPage.newMessage
        };

        this._state.dialogsPage.messagesData.push(newMessage);
        this._state.dialogsPage.newMessage = '';
        this._callSubscriber(this._state);
    },
    _updateMessage(newText) {
        this._state.dialogsPage.newMessage = newText;
        this._callSubscriber(this._state);
    },
    subscribe(observer) {
        this._callSubscriber = observer;
    },
    dispatch(action) {
        if (action.type === 'ADD-POST') {
            this._addPost();
        } else if (action.type === 'UPDATE-POST') {
            this._updatePost(action.text);
        }
        if (action.type === 'SEND-MESSAGE') {
            this._sendMessage();
        } else if (action.type === 'UPDATE-MESSAGE') {
            this._updateMessage(action.newText);
        }
    }
}

window.store = store;
export default store;
