let rerenderTree = () => {
    console.log('State changed');
}

let state = {
    profilePage: {
        postsData: [
        {id: 1, post: 'Го по пиву пацаны'},
        {id: 2, post: ')'}
    ],
        newPost: 'jopa'
    },
    dialogsPage: {
        messagesData: [
        {id: 1, message: 'Гослинг'},
        {id: 2, message: 'Вася'},
        {id: 3, message: 'Путин'}
    ],
        dialogsData: [
        {id: 1, name: 'Гослинг'},
        {id: 2, name: 'Вася'},
        {id: 3, name: 'Путин'},
        {id: 4, name: 'Трамп'},
        {id: 5, name: 'Обама'},
        {id: 6, name: 'Мэнсон'},
        {id: 7, name: 'Антошка'}
    ],
        newMessage: 'ya lomal steklo'
    }
}

window.state = state;

export const addPost = () => {
    let newPost = {
        id: 3,
        post: state.profilePage.newPost
    };

    state.profilePage.postsData.push(newPost);
    state.profilePage.newPost = '';
    rerenderTree();
}

export const updatePost = (text) => {
    state.profilePage.newPost = text;
    rerenderTree();
}

export const sendMessage = () => {
    let newMessage = {
        id: 4,
        message: state.dialogsPage.newMessage
    };

    state.dialogsPage.messagesData.push(newMessage);
    state.dialogsPage.newMessage = '';
    rerenderTree();
}

export const updateMessage = (newText) => {
    state.dialogsPage.newMessage = newText;
    rerenderTree();
}

export const subscribe = (observer) => {
    rerenderTree = observer;
}

export default state;
