let store = {
    _state: {
        profilePage: {
            postsData: [
                {id: 1, post: 'Го по пиву пацаны'},
                {id: 2, post: ')'}
            ],
            newPost: 'jopa'
        },
        dialogsPage: {
            messagesData: [
                {id: 1, message: 'Гослинг'},
                {id: 2, message: 'Вася'},
                {id: 3, message: 'Путин'}
            ],
            dialogsData: [
                {id: 1, name: 'Гослинг'},
                {id: 2, name: 'Вася'},
                {id: 3, name: 'Путин'},
                {id: 4, name: 'Трамп'},
                {id: 5, name: 'Обама'},
                {id: 6, name: 'Мэнсон'},
                {id: 7, name: 'Антошка'}
            ],
            newMessage: 'ya lomal steklo'

        }
    },
    _callSubscriber() {
        console.log('State changed');
    },
    addPost() {
        let newPost = {
            id: 3,
            post: this._state.profilePage.newPost
        };

        this._state.profilePage.postsData.push(newPost);
        this._state.profilePage.newPost = '';
        this._callSubscriber(this._state);
    },
    updatePost(text) {
        this._state.profilePage.newPost = text;
        this._callSubscriber(this._state);
    },
    sendMessage() {
        let newMessage = {
            id: 4,
            message: this._state.dialogsPage.newMessage
        };

        this._state.dialogsPage.messagesData.push(newMessage);
        this._state.dialogsPage.newMessage = '';
        this._callSubscriber(this._state);
    },
}

export const updateMessage = (newText) => {
    state.dialogsPage.newMessage = newText;
    callSubscriber(state);
}

export const subscribe = (observer) => {
    callSubscriber = observer;
}

window.state = this._state;

export default store;
