import {rerenderTree} from "../render";

let state = {
    profilePage: {
        postsData: [
        {id: 1, post: 'Го по пиву пацаны'},
        {id: 2, post: ')'}
    ]
    },
    dialogsPage: {
        messagesData: [
        {message: 'Гослинг'},
        {message: 'Вася'},
        {message: 'Путин'},
        {message: 'Трамп'},
        {message: 'Обама'},
        {message: 'Мэнсон'},
        {message: 'Антошка'}
    ],
        dialogsData: [
        {id: 1, name: 'Гослинг'},
        {id: 2, name: 'Вася'},
        {id: 3, name: 'Путин'},
        {id: 4, name: 'Трамп'},
        {id: 5, name: 'Обама'},
        {id: 6, name: 'Мэнсон'},
        {id: 7, name: 'Антошка'}
    ]
    }
}

export let addPost = (postMessage) => {
    let newPost = {
        id: 5,
        message: postMessage
    };

    state.profilePage.postsData.push(newPost);
    rerenderTree(state);
}

export default state;
